import React, { Component, PropTypes } from 'react';
import '../css/Search.css';


/**
 * Icone e input para pesquisar
 *
 */
class Search extends Component {
    constructor(){
        super();
        this.state = { 
            tarefa: "",
            tarefas: ['Teste1', 'Teste2'],
        }
    }

  render() {
       console.log('tarefa', this.state.tarefa);
       console.log('tarefas', this.state.tarefas);

    return (
      <form className="search-form">
        <input
          type="search"
          onChange={this.handleChange} 
        />
        <button onClick={this.addTarefa}>Add</button>
        <ul>
            {
                this.state.tarefas.map(item => <li> {item} </li>)
            }
        </ul>
      </form>
    );
  }

  handleChange = event => () => {
      console.log('event.target.value', event.target.value);
    this.setState({ tarefa: event.target.value });
  }

  addTarefa = () => {
      this.setState({
        //   tarefa: '',
        tarefas: [].concat(this.state.tarefas, this.state.tarefa)
      })
  } 
}

export default Search;
