import React, { Component } from 'react';
import '../css/Search.css';


/**
 * Icone e input para pesquisar
 *
 */
class Search extends Component {
    constructor(){
        super();
        this.state = { 
            tarefa: "",
            tarefas: [
                {id: 1, name: 'Tarefa 1'},
                {id: 2, name: 'Tarefa 2'},
            ],
        }
    }

  render() {
       console.log('tarefa', this.state.tarefa);
       console.log('tarefas', this.state.tarefas);

    return (
      <form className="search-form">
        <input
          type="search"
          onChange={this.handleChange} 
        />
        <button onClick={this.addTarefa}>Add</button>
        <ul>
            {
                this.state.tarefas.map(tarefa => <li> {tarefa.name} </li>)
            }
        </ul>
      </form>
    );
  }

  handleChange = event => {
    this.setState({ tarefa: { id: Math.random().toString(36).substr(-8) ,name: event.target.value }});
  }

  addTarefa = () => {
      console.log('onclick', this.state.tarefa);
    //   this.setState({
    //     //   tarefa: '',
    //     tarefas: this.state.tarefas.concat(this.state.tarefa),
    //   })
  } 
}

export default Search;
